const RightLeftMessageStyle = theme=>({
    right:{
        display:'flex',
        flexDirection:'row',
        justifyContent:'flex-end',
        margin:10,
        backgroundColor:'#fe2d55'
    },
    left:{
        display:'flex',
        flexDirection:'row',
        justifyContent:'flex-start'
    }
})

export default RightLeftMessageStyle
