import { Button, Card, CardContent, CardHeader, CardMedia, Grid, List, ListItem, ListItemSecondaryAction, ListItemText, Slider, Typography } from '@material-ui/core'
import { grey } from '@material-ui/core/colors'
import { Skeleton } from '@material-ui/lab'
import React from 'react'
import {connect} from 'react-redux'
import {indexProducts} from '../state/actions/ProductAction'
import SparkProductLogo from './SparkProductLogo'
import {Link} from 'react-router-dom'
import {me} from '../../state/actions/usersActions'
import {logout} from '../../../TokenService'
import {withRouter} from 'react-router-dom'
class AccountSetting extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            ageValue:[18,27]
        }
    }
    

    componentDidMount(){
        this.props.indexProducts()
        this.props.me()
    }

   handleChange = (event, newValue) => {
        this.setState({
            ageValue:newValue
        })
      }

  logout = ()=>{
     logout()
     window.location.reload()
     this.props.history.push('/')
  }

    render() {
        return (
            <div style={{display:'flex',flexDirection:'column',alignItems:'center',width:500,margin:25,height:'100%',backgroundColor:'transparent'}}>
               {
                   this.props.loading
                   ?
                        (
                            <Skeleton
                             width={'60%'}
                             height={100}
                             style={{backgroundColor:grey[500]}}
                            />
                        )
                   :
                        (
                            <Grid item md={12} sm={12} xs={12}>
                    <Typography>
                        Account settings
                    </Typography>
                                                
                    <div style={{backgroundColor:'white',marginBottom:20,width:350}}>
                    <List style={{width:'100%'}}>
                                                    <ListItem button>
                                                        <ListItemText
                                                        primary={'Email'}
                                                        />
                                                        <ListItemSecondaryAction>
                                                            <Button 
                                                            variant={'text'}
                                                            color={"primary"}
                                                            style={{textTransform:'none'}}>
                                                                Verify now
                                                            </Button>
                                                        </ListItemSecondaryAction>
                                                    </ListItem>

                                                    <ListItem button>
                                                        <ListItemText
                                                        primary={'Phone number'}
                                                        />
                                                        <ListItemSecondaryAction>
                                                            <Button 
                                                            variant={'text'}
                                                            color={"inherit"}
                                                            style={{textTransform:'none'}}>
                                                                {
                                                                    this.props.user.attribute.phone
                                                                }
                                                            </Button>
                                                        </ListItemSecondaryAction>
                                                    </ListItem>

                                                    <ListItem button>
                                                        <ListItemText
                                                        primary={'Promo code'}
                                                        />
                                                        <ListItemSecondaryAction>
                                                            <Button 
                                                            variant={'text'}
                                                            color={"inherit"}
                                                            style={{textTransform:'none'}}>
                                                                
                                                            </Button>
                                                        </ListItemSecondaryAction>
                                                    </ListItem>
                                                    
                                                    </List>
                                                </div>

                                                <Typography>
                                                    Discovery settings
                                                </Typography>
                                                <div style={{backgroundColor:'white', marginBottom:20}}>

                                                    <List>
                                                    <ListItem button>
                                                        <ListItemText
                                                        primary={'Location'}
                                                        secondary={
                                                            <Slider
                                                            aria-label="custom thumb label"
                                                            defaultValue={80}
                                                            />
                                                        }
                                                        />
                                                        <ListItemSecondaryAction>
                                                            <Button 
                                                            variant={'text'}
                                                            color={"inherit"}
                                                            style={{textTransform:'none'}}>
                                                                {`80 km`}
                                                            </Button>
                                                        </ListItemSecondaryAction>
                                                    </ListItem>

                                                    <ListItem button>
                                                        <ListItemText
                                                        primary={'Age range'}
                                                        secondary={
                                                            <Slider
                                                            value={this.state.ageValue}
                                                            onChange={this.handleChange}
                                                            valueLabelDisplay="auto"
                                                            aria-labelledby="range-slider"/>
                                                            
                                                        }
                                                        />
                                                        <ListItemSecondaryAction>
                                                            <Button 
                                                            variant={'text'}
                                                            color={"inherit"}
                                                            style={{textTransform:'none'}}>
                                                                {this.state.ageValue.map((value,index)=>{
                                                                    if(index===0){
                                                                    return <span>{`${value} -`}</span>
                                                                    }else{
                                                                    return <span>{value}</span>
                                                                    }
                                                                })}
                                                            </Button>
                                                        </ListItemSecondaryAction>
                                                    </ListItem>
                                                    </List>
                                                </div>


                                                <div style={{backgroundColor:'white'}}>
                                                <List>
                                                <ListItem button onClick={this.logout}>
                                                        <ListItemText
                                                        primary={'Logout'}
                                                        />
                                                        
                                                </ListItem>
                                                </List>
                                                </div>                    
                                 </Grid>
                        )
               }
            </div>
        )
    }
}

const mapStateToProps = state=>({
    products:state.authReducer.usersReducer.ProductReducer.products,
    loading:state.authReducer.usersReducer.ProductReducer.loading,
    user:state.userData.user,
    userLoading:state.userData.loading
})

export default withRouter(connect(mapStateToProps,{indexProducts,me})(AccountSetting))
