import React from 'react'
import Pusher from 'pusher-js'
import { CardContent, IconButton, Input, Typography } from '@material-ui/core';
import RightMessage from './RightMessage';
import LeftMessage from './LeftMessage';
import { Send } from '@material-ui/icons';

class Chats extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            chats:[],
            scrollTop:2
        }

        this.chatRef = React.createRef()
        this.inputRef = React.createRef()
        this.messagesEndRef = React.createRef()

    }
    

    componentDidMount(){
        this.scrollToBottom()

        this.setState({
            chats:this.props.chats
        })
        this.chatRef.current.scrollTop = this.state.scrollTop
        let PUSHER_APP_KEY = "52d240141d3de6310e4e"
        let pusher = new Pusher(PUSHER_APP_KEY, {
        cluster: 'ap2',
        forceTLS: true
        });
        
        let channel = pusher.subscribe(`channel-${this.sumId(this.props.user.attribute.id,this.props.chatUser.attribute.id)}`);
        channel.bind(`broadcast-${this.sumId(this.props.user.attribute.id,this.props.chatUser.attribute.id)}`,(data)=>{
        this.showData(data)
        console.log(`message:${data.id}`)

        });
    }

    componentDidUpdate(){
        this.scrollToBottom()

    }

    scrollToBottom = () => {
        this.messagesEnd.scrollIntoView({ behavior: 'smooth' })
      }

    handleClick = ()=>{
        this.chatRef.current.scrollTop +=1
        const message={
            id:22,
            message:this.inputRef.current.value
        }
        console.log(message)
        this.props.chats.push(message)
    }

    sumId = (id1,id2)=>{
        return Number(id1)+Number(id2);
    }

    showData = (data)=>{
        const {chats} = this.state
        chats.push(data)
        this.setState(chats)
    }

    render() {
        return (
            <CardContent>
                <div style={{height:'90%'}} ref={this.messagesEndRef}>
                {
                    this.props.chats.map(chat=>{
                        return chat.sender===this.props.user.attribute.id
                        ?
                            (
                                 <RightMessage chat={chat}/>
                            )
                        :
                            (
                                 <LeftMessage chat={chat}/>
                            )
                    })
                }
                </div>
                <div style={{display:'flex',flexDirection:'row'}}>
                    <Input
                    ref={this.inputRef}
                    style={{width:'90%'}}
                     placeholder={'Write your message...'}
                    />
                    <IconButton onClick={this.handleClick} color={'primary'}>
                        <Send/>
                    </IconButton>
                </div>
            </CardContent>
        )
    }
}

export default Chats
