import {ADD_MEDIA,FETCH_MEDIA} from '../usersConstant'

const initialState = {
    medias:[],
    loading:true
}

export default function(state=initialState,action){
    switch(action.type){
        case FETCH_MEDIA:
            return {
                ...state,
                medias:action.payload,
                loading:false
            }

        default:
                return state
    }
}
