import {useEffect, useState} from 'react'
import axios from 'axios'
import {get, removeToken, set} from "../TokenService";
import {API_AUTH_URL} from "../constants/constants";


function Interceptor() {
    axios.defaults.headers.common['Authorization'] = `Bearer ${get()}`;
    return axios.interceptors.response.use(response => {
        return response;
    }, err => {
        return new Promise((resolve, reject) => {
            const originalReq = err.config;
            if ( err.response.status === 401 && err.config && !err.config.__isRetryRequest )
            {
                originalReq._retry = true;

                let res = fetch(`${API_AUTH_URL}refresh`, {
                    method: 'POST',
                    mode: 'cors',
                    cache: 'no-cache',
                    credentials: 'same-origin',
                    headers: {
                        'Content-Type': 'application/json',
                        'Device': 'device',
                        'Token': localStorage.getItem("token")
                    },
                    redirect: 'follow',
                    referrer: 'no-referrer',
                    body: JSON.stringify({
                        token: get(),
                    }),
                }).then(res => res.json()).then(res => {
                    console.log(res);
                    //set(res.token)
                    //this.setSession({token: res.token, refresh_token: res.refresh});
                    originalReq.headers['Authorization'] = `Bearer ${res.token}`;
                    return axios(originalReq);
                });


                resolve(res);
            }


            return Promise.reject(err);
        });
    });
}

export default Interceptor
