import {FETCH_CHATS} from '../usersConstant'

const initialState = {
    prevChats:[],
    loading:true
}

export default function(state=initialState,action){
    switch(action.type){
        case FETCH_CHATS:
            return {
                ...state,
                prevChats:action.payload,
                loading:false
            }

            default:
                return state
    }
}
