const RightLeftMessageStyle = theme=>({
    right:{
        display:'flex',
        flexDirection:'row',
        justifyContent:'flex-end',
        width:'20vw'
    },
    innerRight:{
        margin:10,
        backgroundColor:'#fe2d55',
        width:'auto',
        color:'white'
    },
    left:{
        display:'flex',
        flexDirection:'row',
        justifyContent:'flex-start'
    }
})

export default RightLeftMessageStyle
