import React from 'react'
import {me} from '../../state/actions/usersActions'
import {connect} from 'react-redux'
import { Button, Card, CardMedia, Grid } from '@material-ui/core'
import { Skeleton } from '@material-ui/lab'
import { grey } from '@material-ui/core/colors'
class AddMedia extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            medias:[]
        }
    }
    

    componentDidMount(){
        this.props.me()
        const {medias} = this.state
        medias.push(this.props.user.attribute.profile_pic_path);
        console.log(`Size: ${medias.length()}`)
        this.setState(medias)
    }

    render() {
        return (
            <div>
                {
                    this.props.loading
                    ?
                        (
                            <Skeleton
                            variant={'rect'}
                            width={400}
                            height={200}
                            style={{backgroundColor:grey[500]}}/>
                        )
                    :
                        (
                            <div style={{display:'flex',flexDirection:'column',marginTop:25,marginBottom:30}}>
                                <Grid container spacing={2}>
                                    {
                                        this.state.medias
                                        .map(media=>(
                                            <Grid item md={4} xs={4} sm={4}>
                                                <Card>
                                                    <CardMedia
                                                     height={200}
                                                     image={media}
                                                    >

                                                    </CardMedia>
                                                </Card>
                                            </Grid>
                                        ))
                                    }
                                </Grid>
                                <div style={{display:"flex",flexDirection:'column',
                                paddingLeft:50,paddingRight:50}}>
                                    <Button 
                                    variant={'contained'} 
                                    color={'primary'} 
                                    size={'medium'}>
                                        Add new media
                                    </Button>
                                </div>
                            </div>
                        )
                }
            </div>
        )
    }
}

const mapStateToProps = state=>({
    user:state.userData.user,
    loading:state.userData.loading,
    response:state.authReducer.utilityReducer.response
})

export default connect(mapStateToProps,{me})(AddMedia)
