import {FETCH_PASSION} from '../usersConstant'

const initialState = {
    passions:[],
    loading:true
}

export default function(state=initialState,action){
    switch(action.type){
        case FETCH_PASSION:
            return {
                ...state,
                passions:action.payload,
                loading:false
            }
    }
}
